DELIMITER //
CREATE TRIGGER `ruf_konflikte` AFTER UPDATE ON `ruf` FOR EACH ROW BEGIN
    DECLARE punkte_diff INT;
    DECLARE target VARCHAR(50);
    DECLARE ratio INT;

    -- Cursor für Konflikte
    DECLARE conflict_cursor CURSOR FOR
        SELECT partei_target, ratio
        FROM ruf_konflikte
        WHERE partei_source = NEW.partei;

    DECLARE CONTINUE HANDLER FOR NOT FOUND SET target = NULL;

    -- Punkteunterschied berechnen
    SET punkte_diff = NEW.amount - OLD.amount;

    IF punkte_diff > 0 THEN
        OPEN conflict_cursor;

        conflict_loop: LOOP
            FETCH conflict_cursor INTO target, ratio;

            IF target IS NULL THEN
                LEAVE conflict_loop;
            END IF;

            -- Berechnung des Punkteabzugs
            VALUES (target, FLOOR(punkte_diff / ratio));
        END LOOP;

        CLOSE conflict_cursor;
    END IF;
END//
DELIMITER ;
